;(function($){
  $(document).ready(function(){

    $(".b-header").headerSticky(), $(".b-scroll-top").scrollTo(), $(".fixedsticky").fixedsticky();

    $(".b-popup_close, .js-form-fake").click(function() {
      if($(".b-popup").hasClass("i-hide")) {
        $('body,html').animate({
          scrollTop: 0
        }, 800);
      }
      $(".b-popup").toggleClass("i-hide");
      $(".fancybox").fancybox();
    });

    $('.date').append('3 ноября');

    
    $(".jq-fancybox-lib").fancybox();

    // Отправка формы
    $('.call-back-form').submit(function(e){
      e.preventDefault();
      var m_method=$(this).attr('method');
      var m_action=$(this).attr('action');
      var m_data=$(this).serialize();
      $.ajax({
        type: m_method,
        url: m_action,
        data: m_data,
        success: function(result){
          $('.modal-title').css("visibility", "hidden");
          $('.call-back-form').css("display", "none");
          $('.form-result').css("display", "block");
        }
      });
    });

  })
})(jQuery);
